    index++;

    // 1. Validación inicial de estructura
    if (!item.data || typeof item.data !== 'string') {
        node.warn(`Ítem ${index}: 'data' no es un string o no existe.`);
        return null;
    }

    // 2. Dividir y validar cantidad de elementos
