        ahtHum: parseFloat(dataValues[5]),    // ahtHumidity (índice 5)
        dust: parseFloat(dataValues[9]),    // dustDensity (índice 9)
        light: parseFloat(dataValues[10]),   // photoResistorValue (índice 10)
        timestamp: parseInt(dataValues[11])      // timestamp (índice 11)
    };

    // 4. Validación de conversiones numéricas
    if (Object.values(rawValues).some(v => isNaN(v))) {
        return null;
    }

    // 5. Crear objeto temporal con timestamp como Date
    const tempItem = {
        "indice": index,
        "Timestamp": new Date(rawValues.timestamp),
        "BMP Temperature": rawValues.bmpTemp,
        "Pressure": rawValues.pressure,
        "Altitude": rawValues.altitude,
        "AHT Temperature": rawValues.ahtTemp,
        "AHT Humidity": rawValues.ahtHum,
        "Dust Density": rawValues.dust,
        "Solar Intensity": rawValues.light
    };

    // 6. Validaciones de rango y consistencia ACTUALIZADAS
    const isValid = (
        (tempItem["BMP Temperature"] >= -40 && tempItem["BMP Temperature"] <= 85) &&      // Rango típico BMP
        (tempItem["Pressure"] >= 300 && tempItem["Pressure"] <= 1100) &&                  // Rango presión atmosférica
        (tempItem["Altitude"] >= 0 && tempItem["Altitude"] <= 5000) &&                    // Rango altitud más realista
